import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Send, Bot, User } from 'lucide-react';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isSending, setIsSending] = useState(false);

  const handleSend = async () => {
    if (inputText.trim() && !isSending) {
      const userMessage = { text: inputText.trim(), sender: 'user' };
      const currentInput = inputText.trim();

      // Immediately clear input and show user message
      setInputText('');
      setMessages(prev => [...prev, userMessage]);
      setIsSending(true);

      try {
        // Start the API request after showing the user message
        const res = await axios.post('http://localhost:8000/chat/', {
          question: currentInput
        });

        const cleanedResponse = res.data.response.replace(/<think>|<\/think>/g, '');
        const botMessage = { text: '', sender: 'bot', isTyping: true };
        setMessages(prev => [...prev, botMessage]);
        simulateTyping(cleanedResponse);
      } catch (error) {
        console.error('Error communicating with the backend:', error);
        setMessages(prev => [...prev, { 
          text: 'Sorry, I encountered an error. Please try again.',
          sender: 'bot'
        }]);
        setIsSending(false);
      }
    }
  };

  const simulateTyping = (text) => {
    let index = 0;
    const interval = setInterval(() => {
      setMessages((prev) => {
        const lastMessage = prev[prev.length - 1];
        if (lastMessage.sender === 'bot' && lastMessage.isTyping) {
          const updatedMessage = {
            ...lastMessage,
            text: lastMessage.text + text[index],
          };
          return [...prev.slice(0, -1), updatedMessage];
        }
        return prev;
      });

      index++;
      if (index >= text.length) {
        clearInterval(interval);
        setMessages((prev) => {
          const lastMessage = prev[prev.length - 1];
          if (lastMessage.sender === 'bot' && lastMessage.isTyping) {
            const updatedMessage = {
              ...lastMessage,
              isTyping: false,
            };
            return [...prev.slice(0, -1), updatedMessage];
          }
          return prev;
        });
        setIsSending(false);
      }
    }, 50);
  };

  useEffect(() => {
    const messageList = document.querySelector('.message-list');
    if (messageList) {
      messageList.scrollTop = messageList.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <div className="bg-white shadow-md py-4">
        <h1 className="text-2xl font-bold text-center text-gray-800">
          Internal AI Assistant
        </h1>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4 message-list">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`flex ${
              msg.sender === 'user' ? 'justify-end' : 'justify-start'
            }`}
          >
            <div
              className={`flex items-start space-x-2 max-w-[80%] ${
                msg.sender === 'user' ? 'flex-row-reverse space-x-reverse' : ''
              }`}
            >
              <div
                className={`p-2 rounded-lg ${
                  msg.sender === 'user'
                    ? 'bg-blue-600 text-white'
                    : 'bg-white text-gray-800'
                } shadow-md`}
              >
                <div className="flex items-center space-x-2">
                  {msg.sender === 'user' ? (
                    <User className="w-4 h-4" />
                  ) : (
                    <Bot className="w-4 h-4" />
                  )}
                  <span>{msg.text}</span>
                  {msg.isTyping && (
                    <span className="animate-pulse">|</span>
                  )}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="p-4 bg-white border-t">
        <div className="max-w-4xl mx-auto flex space-x-4">
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Type your message..."
            className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            disabled={isSending}
          />
          <button
            onClick={handleSend}
            disabled={isSending}
            className={`px-4 py-2 rounded-lg flex items-center space-x-2 ${
              isSending
                ? 'bg-gray-300 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700'
            } text-white transition-colors`}
          >
            <span>{isSending ? 'Sending...' : 'Send'}</span>
            <Send className="w-4 h-4" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
