import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import the CSS file

function App() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isSending, setIsSending] = useState(false); // Track sending state
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (inputText.trim() && !isSending) {
      const userMessage = { text: inputText, sender: 'user' };
      setMessages((prev) => [...prev, userMessage]);

      // Clear the input field immediately
      setInputText('');
      setIsSending(true); // Disable the send button

      try {
        const res = await axios.post('http://localhost:8000/chat/', {
          question: inputText
        });

        const cleanedResponse = res.data.response.replace(/<think>|<\/think>/g, '');

        // Add a placeholder bot message
        const botMessage = { text: '', sender: 'bot', isTyping: true };
        setMessages((prev) => [...prev, botMessage]);

        simulateTyping(cleanedResponse);
      } catch (error) {
        console.error('Error communicating with the backend:', error);
      } finally {
        setIsSending(false); // ✅ Reset sending state no matter what
      }
    }
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const response = await fetch('/your-api-endpoint', {
        method: 'POST',
        body: JSON.stringify({ prompt }),
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      // do something with the response
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false); // ✅ make sure this is always called
    }
  };

  // Simulate typing effect
  const simulateTyping = (text) => {
    let index = 0;
    const interval = setInterval(() => {
      setMessages((prev) => {
        const lastMessage = prev[prev.length - 1];
        if (lastMessage.sender === 'bot' && lastMessage.isTyping) {
          const updatedMessage = {
            ...lastMessage,
            text: lastMessage.text + text[index],
          };
          return [...prev.slice(0, -1), updatedMessage];
        }
        return prev;
      });

      index++;
      if (index >= text.length) {
        clearInterval(interval);
        setMessages((prev) => {
          const lastMessage = prev[prev.length - 1];
          if (lastMessage.sender === 'bot' && lastMessage.isTyping) {
            const updatedMessage = {
              ...lastMessage,
              isTyping: false, // Mark typing as complete
            };
            return [...prev.slice(0, -1), updatedMessage];
          }
          return prev;
        });
      }
    }, 50); // Adjust typing speed (milliseconds per character)
  };

  // Auto-scroll to the bottom of the message list
  useEffect(() => {
    const messageList = document.querySelector('.message-list');
    if (messageList) {
      messageList.scrollTop = messageList.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="chat-container">
      {/* Add a title at the top */}
      <div className="chat-title">
        Internal AI Assistant
      </div>

      <div className="message-list">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            <div className="message-bubble">
              {msg.text}
              {msg.isTyping && <span className="typing-indicator">|</span>}
            </div>
          </div>
        ))}
      </div>
      <div className="input-area">
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Type your message..."
        />
        <button onClick={handleSend} disabled={isSending}>
          {isSending ? 'Sending...' : 'Send'}
        </button>
      </div>
    </div>
  );
}

export default App;
